import axios from "axios";
import { useEffect, useState } from "react";
import AdminHeader from "../components/AdminHeader";
import AdminButton from "../components/AdminButton";
import { useNavigate } from "react-router-dom";
const RoundView=()=>{
const [data,setData]=useState([]);
const navigate=useNavigate();
useEffect(() => {
    fetchuserData();
 }, []); //가져온 회차데이터 실행

 
   const fetchuserData = async () => {     
     try {
         const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/round/view`);
         setData(response.data);

     }catch (error) {
    //  if(error.response.status===500){
     //  alert(error.response.data.message);
      }
     }  // api로 회차 정보 받아오는 함수
return(
    <div className="AdminEntire">
        <AdminHeader/>
        <div className="UserText">회차 조회</div>
        <div className="RoundAll">
        <div className="RoundMenuList">
        <div className="RoundMenuItem1">회차 이름</div> <div className="RoundMenuItem2">수정하기</div> <div className="RoundMenuItem2">삭제하기</div>
        </div>
        {data&&data.map((it)=>{
            return(
            <div className="RoundList">
          <div className="RoundItem1">{it.roundName}</div>
          <AdminButton text="수정하기" className="RoundItem2"/>
          <AdminButton text="삭제하기" className="RoundItem2"/>
          </div>
            )
        })}
    </div>
    <AdminButton text="생성" className="RoundCreate" onClick={()=>navigate()}/>
    </div>
)
};
export default RoundView;
