import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import AdminHeader from "../components/AdminHeader";
import AdminButton from "../components/AdminButton";
const UserEdit=()=>{
    const [data,setData]=useState({
      userId:"",
      passwd:"",
      userName:"",
      userEmail:""
    });
    const {id}=useParams();
    useEffect(() => {
      fetchuserData();
   }, []);

   
     const fetchuserData = async () => {     
       try {
           const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/listView`);
           const userData = response.data.find(item => item.userId === id);
           setData({
            userId: userData.userId,
            passwd: userData.passwd,
            userName: userData.userName,
            userEmail: userData.userEmail
        });
       } catch (error) {
        console.error('Error fetching data:', error);
       }
   };
   const handleChange = (event) => {
    const { name, value } = event.target;
    setData(prevState => ({
        ...prevState,
        [name]: value
    }));
};

return (
    <div className="AdminEntire">
       <AdminHeader/>
       
        <div>사용자 정보 수정</div>
        <div className="UserEdit">
          <div>아이디</div>
      <div className="EditId"><input name="userId" value={data.userId} onChange={handleChange} /><div>중복</div></div>  
        <div>비밀번호</div>
        <input name="passwd" value={data.passwd} onChange={handleChange} />
        <div>닉네임</div>
        <input name="userName" value={data.userName} onChange={handleChange} />
        <div>이메일</div>
        <input name="userEmail" value={data.userEmail} onChange={handleChange} />  </div>
       <div className="UserEditButton"> <AdminButton text="취소" className="UserEditButtonDetail1"/>
        <AdminButton text="제출" className="UserEditButtonDetail2"/></div>
      
    </div>
);

};
export default UserEdit;
