import { useEffect, useState } from "react";
import AdminHeader from "../components/AdminHeader";
import axios from 'axios';
import { Link, useNavigate } from "react-router-dom";
import AdminButton from "../components/AdminButton";
const UserView=()=>{
   const navigate=useNavigate();
    const [data,setData]=useState([]);
    useEffect(() => {
      userData();

   }, []);


     const userData = async () => {
       
       try {

           const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/listView`);
           setData(response.data);
           
       } catch (error) {
        console.error('Error fetching data:', error);
       }
   };
const handleDelete=async(userId)=>{
  Swal.fire({
    title: '정말로 삭제하시겠습니까?',
    text: "이 작업은 되돌릴 수 없습니다!",
    icon: 'warning',
    showCancelButton: true,
    confirmButtonColor: '#3085d6',
    cancelButtonColor: '#d33',
    confirmButtonText: '네, 삭제합니다!',
    cancelButtonText: '아니오, 취소합니다!'
}).then((result) => {
    if (result.isConfirmed) {
      axios.post(`${process.env.REACT_APP_API_URL}/admin/users/delete`, {
        userId:userId,
        
     })
     .then(response => {        
         alert("계정이 삭제되었습니다.");
         window.location.reload();
     })
     .catch(error => {
         alert("계정 삭제에 실패했습니다.");
         window.location.reload();
     });
    } else if (result.dismiss === Swal.DismissReason.cancel) {
        // 취소 로직
    }
});

}
 return(
    <div className="AdminEntire">
        <AdminHeader/>
        <div className="UserText">사용자 조회</div>
        <div className="UserAll">
        <div className="UserMenuList">
            <div className="UserItem1">ID</div> <div className="UserItem2">비밀번호</div>
             <div className="UserItem3">닉네임</div> <div className="UserItem4">이메일</div> <div className="UserItem5">식별코드</div> 
             <div className="UserItem5">수정하기</div> <div className="UserItem5">삭제하기</div>
        </div>
        <div>
        {data&&data.map((it)=>{
           return(
            <div className="UserList">
            <div className="UserItem1">{it.userId}</div>
            <div className="UserItem2">{it.passwd}</div>
            <div className="UserItem3">{it.userName}</div>
            <div className="UserItem4">{it.userEmail}</div>
            <div className="UserItem5">{it.is_admin}</div>
            <Link to={`/admin/user/edit/${it.userId}`} className="UserItem6">수정하기</Link>
            <AdminButton text="삭제하기" onClick={()=>handleDelete(it.userId)} className="UserItem6"/>
            </div>
           )
        })}
        </div>
        </div>
    </div>
 )
};
export default UserView;
