import React, { useEffect, useState } from 'react';
import AdminHeader from "../components/AdminHeader";
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
ChartJS.register(ArcElement, Tooltip, Legend);
const ViewRate=()=>{
  const [subject,setSubject]=useState([])
  const [rate,setRate]=useState([
    {
     subjectId:1,
     countSolveTimes:87,
     countWrgTimes:5
    },
    {
      subjectId:2,
      countSolveTimes:135,
      countWrgTimes:7
    },
    {
      subjectId:3,
      countSolveTimes:9665,
      countWrgTimes:465
    }
  ]);

  useEffect(() => {
    fetchsubjectData();
 }, []); //가져온 과목데이터 실행

 
   const fetchsubjectData = async () => {     
     try {
         const response = await axios.get(`/admin/subject/listView`,{
          headers:{
           'Authorization':token
         }
       })
       if(response)
         setSubject(response.data.subjectList);

     }catch (error) {
      if(error.response.status===500){
       alert(error.response.data.message);
      }
      else if(error.response.status===401 || error.response.status===403){
        alert(error.response.data.message);
        sessionStorage.removeItem("token");
        navigate("/admin/login");
       }
     }  }// api로 과목 정보 받아오는 함수
     const labelData=subject.map((it)=>rate.subjectId==subject.subjectId)
const data = {
    labels: [labelData],
    datasets: [
      {
        label: 'My First Dataset',
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: [
          'rgb(255, 99, 132)',
          'rgb(54, 162, 235)',
          'rgb(255, 205, 86)',
          'rgb(75, 192, 192)',
          'rgb(153, 102, 255)',
          'rgb(255, 159, 64)',
        ],
        hoverOffset: 4,
      },
    ],
  };
  const options = {
    maintainAspectRatio: false,
    width: 100, 
    height: 50, 
};

return(
    <div className="AdminEntire">
        <AdminHeader/>
        <div className="UserText">과목별 오답률 통계</div>
        <div className="PieChart">
        <Pie data={data} options={options}/></div>
    </div>
)
};
export default ViewRate;
