import { useNavigate } from "react-router-dom";
import AdminButton from "../../components/AdminButton";
import AdminHeader from "../../components/AdminHeader";
import { useState } from "react";
import axios from "axios";
const AiPrompt=()=>{
    const [data,setData]=useState("");
    const navigate=useNavigate();
    const token=sessionStorage.getItem("token")
    const handleSubmit=async()=>{
        axios.post(`/admin/prompt_manager`,{
            prompt:data
        }, {
            headers:{
             'Authorization':token
           }
         }).then(response => {        
            alert("전송 성공했습니다");
            window.location.reload();
        })
        .catch(error => {
           if(error.response.status===500){
             alert(error.response.data.message);
           }
          else if(error.response.status===401 || error.response.status===403){
             alert(error.response.data.message);
             sessionStorage.removeItem("token");
             navigate("/admin/login");
            }
        });
       }  // axios 이용하여 aipromt 전송


       useEffect(() => {
        fetchkeywordData();
     }, []); //가져온 키워드데이터 실행
    
       const fetchkeywordData = async () => {     
         try {
             const response = await axios.get(`/admin/keyword/listView`, {
              headers:{
               'Authorization':token
             }
           })
             const keywordData = response.data.keyword.find(item => item.keywordId ==id);
             setData({
                keywordId:keywordData.keywordId,
                keywordName:keywordData.keywordName,
                keywordMean:keywordData.keywordMean});
        
    
         }catch (error) {
          if(error.response.status===500){
          alert(error.response.data.message);
          }
          else if(error.response.status===401 || error.response.status===403){
            alert(error.response.data.message);
            sessionStorage.removeItem("token");
            navigate("/admin/login");
           }
         } } // api로 키워드 정보 받아오는 함수
return(
    <div className="AdminEntire">
        <AdminHeader/>
        <div className="UserText">AiPrompt 관리</div>
        <textarea className="AiPromptBox" onChange={(e)=>setData(e.target.value)} style={{ resize: 'none' }}/>
        <div className="AiPromptButtonList">
        <AdminButton text="이전" className="AiPromptButton" onClick={()=>navigate(-1)}/>
        <AdminButton text="완료" className="AiPromptButton" onClick={handleSubmit}/></div>
        </div>
)
}
export default AiPrompt;
