             const userData = response.find(item => item.quizId === id);
             setData(userData);
             console.log(userData);

         }catch (error) {
         // if(error.response.status===500){
           //alert(error.response.data.message);
          }
         } // api로 수정 정보 받아오는 함수
return(
    <div className="AdminEntire">
    <AdminHeader/>
    </div>
)
};
export default QuizEdit;
