import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import AdminHeader from "../components/AdminHeader";
const UserEdit=()=>{
    const [data,setData]=useState({
      userId:"",
      passwd:"",
      userName:"",
      userEmail:""
    });
    const {id}=useParams();
    useEffect(() => {
      fetchuserData();
   }, []);

   
     const fetchuserData = async () => {     
       try {
           const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/listView`);
           const userData = response.data.find(item => item.userId === id);
           setData({
            userId: userData.userId,
            passwd: userData.passwd,
            userName: userData.userName,
            userEmail: userData.userEmail
        });
       } catch (error) {
        console.error('Error fetching data:', error);
       }
   };
   const handleChange = (event) => {
    const { name, value } = event.target;
    setData(prevState => ({
        ...prevState,
        [name]: value
    }));
};

return (
    <div className="AdminEntire">
       <AdminHeader/>
       <div></div>
        <div>사용자 정보 수정</div>
        <input name="userId" value={data.userId} onChange={handleChange} />
        <input name="passwd" value={data.passwd} onChange={handleChange} />
        <input name="userName" value={data.userName} onChange={handleChange} />
        <input name="userEmail" value={data.userEmail} onChange={handleChange} />
    </div>
);

};
export default UserEdit;
