import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const AdminHeader=()=>{
    useEffect(() => {
        const storedLogin = localStorage.getItem("login");
        if (storedLogin === "true") {
            setIslogin(true);
        }
    }, []);
    const [islogin,setIslogin]=useState(false); 
    localStorage.setItem("login",islogin);
    const handleLoginClick = () => {
        setIslogin(newLoginState);
        localStorage.setItem("login", newLoginState.toString());
    };

 return(
    <div className="Header">
    <img src={process.env.PUBLIC_URL + '/MainLogo.png'} width = '50px' className="HeaderLogo" />
    <div className="HeaderItem1">문보</div>
    {islogin==false?
    <div className="HeaderItem2" onClick={handleLoginClick}> Login </div>
    //<Link to='/admin/login' className="HeaderItem2" >Login</Link>
    :<div className="HeaderItem3" >
     <div className="HeaderItem4">회원관리</div> 
     <div className="HeaderItem4">기출문제</div>
     <div className="HeaderItem4">키워드/과목/회차</div>
     <div className="HeaderItem4">통계/평가</div>
     <div className="HeaderItem4">토큰사용량</div>
     <div onClick={handleLoginClick} className="HeaderItem4">로그아웃</div>
     <div className="HeaderItem5">관리자</div> </div>}
    </div>
    
 )
};
export default AdminHeader;
