import { useEffect, useState } from "react";
import AdminButton from "../components/AdminButton";
import AdminHeader from "../components/AdminHeader";
import { useNavigate } from "react-router-dom";
import axios from "axios";
const AdminLogin=()=>{
    const [id,setId]=useState();
    const [password,setPassword]=useState();
    const navigate=useNavigate()
    const [data,setData]=useState();
    useEffect(() => {
       userData();
      
    }, []); // 사용자정보 가져오기 실행하는 함수
 
 
    const userData =  () => {
      try {
          const response = axios.get(`${process.env.REACT_APP_API_URL}/admin/test`);
          
          if (response.status >= 200 && response.status < 300) {
              // 성공적인 응답인 경우
              setData(response.data); // 실제 데이터를 설정
          } else {
              // 오류 응답인 경우
              console.error('Error fetching data:', response.statusText);
              // 여기에서 오류 처리를 수행하거나 사용자에게 알림을 표시할 수 있음
          }
      } catch (error) {
          // 네트워크 오류나 기타 오류 발생 시
          console.error('Error fetching data:', error);
          // 여기에서 오류 처리를 수행하거나 사용자에게 알림을 표시할 수 있음
      }
  };

  const handleSubmit=async()=>{

    axios.post(`${process.env.REACT_APP_API_URL}/admin/users/login`, {
       userId:id,
       passwd:password,
       
  })
     .then(response => {        
        const token = response.headers.authorization;   
     })
     .catch(error=> {
        console.log(error)
         alert("로그인에 실패했습니다.");
     });
     navigate("/");
    }  // axios 이용하여 로그인하는 함수

 return(
    <div className="AdminLogin">
        <AdminHeader/>
        <div className="LoginText">관리자용 로그인</div>
        <input placeholder="아이디" value={id} onChange={(e)=>setId(e.target.value)} className="LoginInput"></input>
        <input placeholder="비밀번호" value={password} onChange={(e)=>setPassword(e.target.value)} className="LoginInput"></input>
        <AdminButton text="로그인" className="LoginButton" onClick={handleSubmit} />
        <div>{data}</div>
        
    </div>
 )
};
export default AdminLogin;
