import { BrowserRouter, Routes,Route } from 'react-router-dom';
import './App.css';
import Home from './page/Home';
import AdminLogin from './page/AdminLogin';
import UserView from './page/UserView';
import UserEdit from './page/UserEdit';
import QuizView from './page/QuizView';
import QuizEdit from './page/QuizEdit';
import ContentSelect from './page/ContentSelect';
import RoundView from './page/RoundView';
import RoundCreate from './page/RoundCreate';

function App() {


  return (

    <BrowserRouter>
    <div className="App">

<Routes>
<Route path='/' element={<Home/>}/>
<Route path='/admin'>
  <Route path='login' element={<AdminLogin/>}/>

  <Route path='user/view' element={<UserView/>}/>
  <Route path='user/edit/:id' element={<UserEdit/>}/>

  <Route path='quiz/view' element={<QuizView/>}/>
  <Route path="quiz/edit/:id" element={<QuizEdit/>}/>

  <Route path='content/select' element={<ContentSelect/>}/>

  <Route path="round/view" element={<RoundView/>}/>
  <Route path='round/create'element={<RoundCreate/>}/>
</Route>
</Routes>
    </div>
    </BrowserRouter>



  );
}

export default App;
