import axios from "axios";
import { useEffect, useState } from "react";
import AdminHeader from "../components/AdminHeader";
import AdminButton from "../components/AdminButton";
const RoundView=()=>{
const [data,setData]=useState([]);
useEffect(() => {
    fetchuserData();
 }, []); //가져온 회차데이터 실행

 
   const fetchuserData = async () => {     
     try {
         const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/round/view`);
         setData(response.data);

     }catch (error) {
    //  if(error.response.status===500){
     //  alert(error.response.data.message);
      }
     }  // api로 회차 정보 받아오는 함수
return(
    <div className="AdminEntire">
        <AdminHeader/>
        <div className="RoundText">회차 조회</div>
        <div className="RoundAll">
        <div className="RoundMenuList">
        <div>회차 이름</div> <div>수정하기</div> <div>삭제하기</div>
        </div>
        {data&&data.map((it)=>{
            return(
            <div>
          <div>{it.roundName}</div>
          <AdminButton text="수정하기"/>
          <AdminButton text="삭제하기"/>
          </div>
            )
        })}
    </div>
    </div>
)
};
export default RoundView;
