import { useEffect, useState } from "react";
import AdminHeader from "../components/AdminHeader";
import axios from 'axios';
import AdminButton from "../components/AdminButton";
const UserView=()=>{
   
    const [data,setData]=useState([]);
    useEffect(() => {
      userData();

   }, []);
   
     const userData = async () => {
       
       try {

           const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/listView`);
           setData(response.data);
           
       } catch (error) {
        console.error('Error fetching data:', error);
       }
   };
 return(
    <div>
        <AdminHeader/>
        <div>사용자 조회</div>
        <div className="">
        <div className="UserList">
            <div className="UserItem">ID</div> <div className="UserItem">비밀번호</div>
             <div className="UserItem">닉네임</div> <div className="UserItem">이메일</div> 
             <div className="UserItem">수정하기</div> <div className="UserItem">삭제하기</div>
        </div>
        <div>
        {data&&data.map((it)=>{
           return(
            <div className="UserList">
            <div className="UserItem">{it.userId}</div>
            <div className="UserItem">{it.passwd}</div>
            <div className="UserItem">{it.userName}</div>
            <div className="UserItem">{it.userEmail}</div>
            <div className="UserItem">수정하기</div>
            <div className="UserItem">삭제하기</div>
            </div>
           )
        })}
        </div>
        </div>
    </div>
 )
};
export default UserView;
