import { useParams } from "react-router-dom";
import AdminHeader from "../components/AdminHeader";
import { useEffect, useState } from "react";
import axios from "axios";
const QuizEdit=()=>{
    const {id}=useParams();
   const [data,setData]=useState({
        quizId:"",
        subjectId:"",
        roundId:"",
        keywordId:"",
        quizContent:"",
        quizImg:"",
        answ:"",
         r_answ:"",
         wrgAnsw_explanation:"",
      })
    useEffect(() => {
        fetchuserData();
     }, []); //가져온 수정정보데이터 실행
  
     
       const fetchuserData = async () => {     
         try {
             const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/quiz/view`);
             const userData = response.data.find(item => item.quizId ==id);
             setData({
                quizId:userData.quizId,
                subjectId:userData.subjectId,
                roundId:userData.roundId,
                keywordId:userData.keywordId,
                quizContent:userData.quizContent,
                quizImg:userData.quizImg,
                answ:JSON.parse(userData.answ),
                r_answ:userData.r_answ,
                wrgAnsw_explanation:userData.wrgAnsw_explanation,
             });

         }catch (error) {
         // if(error.response.status===500){
           //alert(error.response.data.message);
          }
         } // api로 수정 정보 받아오는 함수
         
   const handleChange = (event) => {
    const { name, value } = event.target;
    setData(prevState => ({
        ...prevState,
        [name]: value
    }));
}; //정보 수정 입력하면 바뀌는 함수
return(
    <div className="AdminEntire">
    <AdminHeader/>
    <div>수정하기</div>
    <input name="quizContent" value={data.quizContent} onChange={handleChange}></input> 
    <img src={data.quizImg} />

    <textarea 
            name="answ" 
            value={JSON.stringify(data.answ)} 
            onChange={handleChange} 
            style={{ width: '100%', height: '200px' }} 
        />
        <input name="r_answ" value={data.r_answ} onChange={handleChange}></input> 
        <input name="wrgAnsw_explanation" value={data.wrgAnsw_explanation}onChange={handleChange}></input> 
    </div>
)
};
export default QuizEdit;
