import { useEffect, useState } from "react";
import AdminHeader from "../components/AdminHeader";
import axios from "axios";
import AdminButton from "../components/AdminButton";
import { Link } from "react-router-dom";
const QuizView=()=>{
    const [quiz,setQuiz]=useState([]);
    useEffect(() => {
        fetchuserData();
     }, []); //가져온 사용자데이터 실행
  
     
       const fetchuserData = async () => {     
         try {
             const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/quiz/view`);
             setQuiz(response.data);

         }catch (error) {
          if(error.response.status===500){
           alert(error.response.data.message);
          }
         }
     };  // api로 사용자 전체 정보 받아오는 함수
  return(
    <div className="AdminEntire">
    <AdminHeader/>
    <div className="UserText">기출문제 조회</div>
    <div className="QuizAll">
    <div className="UserMenuList">
        <div className="UserItem1">ID</div> <div className="UserItem2">문제</div>   
         <div className="UserItem5">수정하기</div> <div className="UserItem5">삭제하기</div>
    </div>
    <div>
    {quiz&&quiz.map((it)=>{
       return(
        <div className="UserList">         
        <div className="UserItem1">{it.quizId}</div>
        <div className="UserItem1">{it.quizContent}</div> 
        <Link to={`/admin/user/edit/${it.userId}`} className="UserItem6">수정하기</Link>
        <AdminButton text="삭제하기"  className="UserItem6"/>
        </div>
       )
    })}
    </div>
    </div>
</div>
  )
};
export default QuizView;
