import { useEffect, useState } from "react";
import AdminButton from "../components/AdminButton";
import AdminHeader from "../components/AdminHeader";
import { useNavigate } from "react-router-dom";
import axios from "axios";
const AdminLogin=()=>{
    const [id,setId]=useState();
    const [password,setPassword]=useState();
    const navigate=useNavigate()
    const [data,setData]=useState();
    useEffect(() => {
       userData();
 
    }, []); // 사용자정보 가져오기 실행하는 함수
 
 
      const userData = async () => {
        
        try {
 
            const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/test`);
            setData(response.data);
            console.log(response)
            
        } catch (error) {
         console.error('Error fetching data:', error);
        }
    }; //api로 전체 사용자 정보 가져오는 함수
 

  const handleSubmit=async()=>{

    axios.post(`${process.env.REACT_APP_API_URL}/admin/users/login`, {
       userId:id,
       passwd:password,
       
  })
     .then(response => {        
        const token = response.headers.authorization;   
     })
     .catch(error=> {
        console.log(error)
         alert("로그인에 실패했습니다.");
     });
     navigate("/");
    }  // axios 이용하여 로그인하는 함수

 return(
    <div className="AdminLogin">
        <AdminHeader/>
        <div className="LoginText">관리자용 로그인</div>
        <input placeholder="아이디" value={id} onChange={(e)=>setId(e.target.value)} className="LoginInput"></input>
        <input placeholder="비밀번호" value={password} onChange={(e)=>setPassword(e.target.value)} className="LoginInput"></input>
        <AdminButton text="로그인" className="LoginButton" onClick={handleSubmit} />
        <div>{data}</div>
        
    </div>
 )
};
export default AdminLogin;
