import { useEffect, useState } from "react";
import AdminHeader from "../components/AdminHeader";
import axios from 'axios';
import AdminButton from "../components/AdminButton";
const UserView=()=>{
   
    const [data,setData]=useState([]);
    useEffect(() => {
      userData();

   }, []);
   
     const userData = async () => {
       
       try {

           const response = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/listView`);
           console.log('Received data:', response.data);
          
           setData(response.data);
           
       } catch (error) {
        if (error.response) {
            // 요청이 이루어졌으며, 서버가 상태 코드와 함께 응답을 했습니다.
            console.log("Response data:", error.response.data);
            console.log("Response status:", error.response.status);
            console.log("Response headers:", error.response.headers);
        } else if (error.request) {
            // 요청이 이루어졌으나 응답을 받지 못했습니다.
            console.log("No response:", error.request);
        } else {
            // 요청을 만드는 도중 오류가 발생했습니다.
            console.log("Error", error.message);
        }
       }
   };
 return(
    <div>
        <AdminHeader/>
       
        <div>사용자 조회</div>
        <div>
            <div>ID</div> <div>비밀번호</div> <div>닉네임</div> <div>이메일</div> <div>수정하기</div> <div>삭제하기</div>
        </div>
        <div>
        {data&&data.map((it)=>{
           return(
            <div>{it.userId}</div>
           )
        })}
        </div>
    </div>
 )
};
export default UserView;
